import React, {useState} from 'react'
import './Testinomials.css'
import {testimonialsData} from '../../data/testimonialsData.js'
// import t-image1 from '../../assets/t-image1.png'
import heart from '../../assets/heart.png'


const Testinomials = () => {

  const [selected, setSelected]=useState(0);
  return (
    <div className='Testinomials'>
      <div className='leftside'>
        <span className='first' >Testinomials</span>
        <span className='three'>WHAT THEY</span>
        <span className='herotext'>SAY ABOUT US</span>
        <span style={{color:"white"}}>
            {testimonialsData[selected].review}
        </span>
        <span style={{color:"white"}}>
          <span style={{color:'var(--orange)'}}>
            {testimonialsData[selected].name}
          </span>{" "}
          -{" "} {testimonialsData[selected].status}
        </span>
      </div>
      <div className='rightside'>
        <div><img src={t-image1} alt="" /></div>
        <div></div>
      </div>
    </div>
  )
}

export default Testinomials
