import React from 'react'
import './Join.css'
import emailjs from '@emailjs/browser'

const Join = () => {
    const form = useRef()

    const sendEmail = (e) => {
        e.preventDefault();
    
        emailjs.sendForm('service_djp224r', 'YOUR_TEMPLATE_ID', form.current, 'YOUR_PUBLIC_KEY')
          .then((result) => {
              console.log(result.text);
          }, (error) => {
              console.log(error.text);
          });
      };

  return (
    <div className='Join'>
        <div className="herotext">
            <div><span className='hollowtext'>READY TO</span><span style={{color:'white'}}> LEVEL UP</span></div>
            <div><span>YOUR BODY</span><span className='hollowtext'>  WITH US ?</span></div>
          </div>
        <div className='rightpart'>
            <form ref={form} onSubmit={sendEmail}>
            <input type="text" name="user" placeholder='Enter your e-mail address ' className='input'/>
            <button className='btn' id='join'>JOIN NOW</button>
            </form>
        </div>
    </div>
  )
}

export default Join
